    private void btnTrocaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTrocaActionPerformed
        // TODO add your handling code here:
        String a = "";
        for (int i = 0; i < 100; i++) {
             a = a + "a";
            lblTitle.setText(a);
        }
        
    }//GEN-LAST:event_btnTrocaActionPerformed

